/**
 * All annotations imported from cfn-lint
 */
export interface CfnLintFileSchema {
  /**
   * Resource types that are in this map are stateful
   *
   * There is more information in the structure this maps to.
   */
  readonly StatefulResources: { readonly ResourceTypes: Record<string, CfnLintStatefulSchema> };
}

/**
 * Extra information on stateful resource types
 */
export interface CfnLintStatefulSchema {
  /**
   * Whether or not a Delete operation requires the resource to be empty
   *
   * @default false
   */
  readonly DeleteRequiresEmptyResource?: boolean;
}

import { loadCannedMetricsFile, MetricTemplate } from './canned-metrics/canned-metrics-schema';

export type NonEmptyArray<T> = [T, ...T[]];

/**
 * A single canned service metric
 *
 * These are kindly provided to us by the good people of CloudWatch Explorer.
 */
export interface CannedMetric {
  /**
   * Metric namespace
   */
  readonly namespace: string;

  /**
   * Metric name
   */
  readonly metricName: string;

  /**
   * List of all possible dimension permutations for this metric
   *
   * Most metrics will have a single list of strings as their one set of
   * allowed dimensions, but some metrics are emitted under multiple
   * combinations of dimensions.
   */
  readonly dimensions: NonEmptyArray<string[]>;

  /**
   * Suggested default aggregration statistic
   *
   * Not always the most appropriate one to use! These defaults have
   * been classified by people and they generally just pick "Average"
   * as the default, even if it doesn't make sense.
   *
   * For example: for event-based metrics that only ever emit `1`
   * (and never `0`) the better statistic would be `Sum`.
   *
   * Use your judgement based on the type of metric this is.
   */
  readonly defaultStat: string;
}

/**
 * Return the list of canned metrics for the given service
 */
export function cannedMetricsForService(cloudFormationNamespace: string): CannedMetric[] {
  // One metricTemplate has a single set of dimensions, but the same metric NAME
  // may occur in multiple metricTemplates (if it has multiple sets of dimensions)
  const metricTemplates = cannedMetricsIndex()[cloudFormationNamespace] ?? [];

  // First construct almost what we need, but with a single dimension per metric
  const metricsWithDuplicates = flatMap(metricTemplates, metricSet => {
    const dimensions = metricSet.dimensions.map(d => d.dimensionName);
    return metricSet.metrics.map(metric => ({
      namespace: metricSet.namespace,
      dimensions,
      metricName: metric.name,
      defaultStat: metric.defaultStat,
    }));
  });

  // Then combine the dimensions for the same metrics into a single list
  return groupBy(metricsWithDuplicates, m => `${m.namespace}/${m.metricName}`).map(metrics => ({
    namespace: metrics[0].namespace,
    metricName: metrics[0].metricName,
    defaultStat: metrics[0].defaultStat,
    dimensions: Array.from(dedupeStringLists(metrics.map(m => m.dimensions))) as any,
  }));
}

type CannedMetricsIndex = Record<string, MetricTemplate[]>;

let cannedMetricsCache: CannedMetricsIndex | undefined;

/**
 * Load the canned metrics file and process it into an index, grouped by service namespace
 */
function cannedMetricsIndex() {
  if (cannedMetricsCache === undefined) {
    cannedMetricsCache = {};
    for (const group of loadCannedMetricsFile()) {
      for (const metricTemplate of group.metricTemplates) {
        const [aws, service] = metricTemplate.resourceType.split('::');
        const serviceKey = [aws, service].join('::');
        (cannedMetricsCache[serviceKey] ?? (cannedMetricsCache[serviceKey] = [])).push(metricTemplate);
      }
    }
  }
  return cannedMetricsCache;
}

function flatMap<A, B>(xs: A[], fn: (x: A) => B[]): B[] {
  return Array.prototype.concat.apply([], xs.map(fn));
}

function groupBy<A>(xs: A[], keyFn: (x: A) => string): Array<NonEmptyArray<A>> {
  const obj: Record<string, NonEmptyArray<A>> = {};
  for (const x of xs) {
    const key = keyFn(x);
    if (key in obj) {
      obj[key].push(x);
    } else {
      obj[key] = [x];
    }
  }
  return Object.values(obj);
}

function* dedupeStringLists(xs: string[][]): IterableIterator<string[]> {
  const seen = new Set<string>();
  for (const x of xs) {
    x.sort();
    const key = `${x.join(',')}`;
    if (!seen.has(key)) {
      yield x;
    }
    seen.add(key);
  }
}

/**
 * Get the canned metrics source file
 */
export function loadCannedMetricsFile(): CannedMetricsFile {
  // eslint-disable-next-line @typescript-eslint/no-require-imports
  return require('./services.json');
}

/**
 * Schema definitions for the accompanying file "services.json".
 */
export type CannedMetricsFile = MetricInfoGroup[];

export interface MetricInfoGroup {
  /**
   * List of metric templates
   */
  readonly metricTemplates: MetricTemplate[];
}


export interface MetricTemplate {
  /**
   * CloudFormation resource name
   */
  readonly resourceType: string;

  /**
   * Metric namespace
   */
  readonly namespace: string;

  /**
   * Set of dimensions for this set of metrics
   */
  readonly dimensions: Dimension[];

  /**
   * Set of metrics these dimensions apply to
   */
  readonly metrics: Metric[];
}

/**
 * Dimension for this set of metric templates
 */
export interface Dimension {
  /**
   * Name of the dimension
   */
  readonly dimensionName: string;

  /**
   * A potential fixed value for this dimension
   *
   * (Currently unused by the spec reader, but could be used)
   */
  readonly dimensionValue?: string;
}

/**
 * A description of an available metric
 */
export interface Metric {
  /**
   * Name of the metric
   */
  readonly name: string;

  /**
   * Default (suggested) statistic for this metric
   */
  readonly defaultStat: string;
}

import * as crypto from 'crypto';
import { CfnLintFileSchema } from './_private_schema/cfn-lint';
import * as schema from './schema';
export { schema };
export * from './canned-metrics';

/**
 * The complete AWS CloudFormation Resource specification, having any CDK patches and enhancements included in it.
 */
export function specification(): schema.Specification {
  // eslint-disable-next-line @typescript-eslint/no-require-imports
  return require('../spec/specification.json');
}

/**
 * Return the resource specification for the given typename
 *
 * Validates that the resource exists. If you don't want this validating behavior, read from
 * specification() directly.
 */
export function resourceSpecification(typeName: string): schema.ResourceType {
  const ret = specification().ResourceTypes[typeName];
  if (!ret) {
    throw new Error(`No such resource type: ${typeName}`);
  }
  return ret;
}

/**
 * Get the resource augmentations for a given type
 */
export function resourceAugmentation(typeName: string): schema.ResourceAugmentation {
  const fileName = typeName.replace(/::/g, '_');
  try {
    // eslint-disable-next-line @typescript-eslint/no-require-imports
    return require(`./augmentations/${fileName}.json`);
  } catch (e) {
    return {};
  }
}

/**
 * Get the resource augmentations for a given type
 */
export function cfnLintAnnotations(typeName: string): schema.CfnLintResourceAnnotations {
  // eslint-disable-next-line @typescript-eslint/no-require-imports
  const allAnnotations: CfnLintFileSchema = require('../spec/cfn-lint.json');

  return {
    stateful: !!allAnnotations.StatefulResources.ResourceTypes[typeName],
    mustBeEmptyToDelete: allAnnotations.StatefulResources.ResourceTypes[typeName]?.DeleteRequiresEmptyResource ?? false,
  };
}

/**
 * Return the property specification for the given resource's property
 */
export function propertySpecification(typeName: string, propertyName: string): schema.Property {
  const ret = resourceSpecification(typeName).Properties![propertyName];
  if (!ret) {
    throw new Error(`Resource ${typeName} has no property: ${propertyName}`);
  }
  return ret;
}

/**
 * The list of resource type names defined in the ``specification``.
 */
export function resourceTypes() {
  return Object.keys(specification().ResourceTypes);
}

/**
 * The list of namespaces defined in the ``specification``, that is resource name prefixes down to the second ``::``.
 */
export function namespaces() {
  return Array.from(new Set(resourceTypes().map(n => n.split('::', 2).join('::'))));
}

/**
 * Obtain a filtered version of the AWS CloudFormation specification.
 *
 * @param filter the predicate to be used in order to filter which resource types from the ``Specification`` to extract.
 *         When passed as a ``string``, only the specified resource type will be extracted. When passed as a
 *         ``RegExp``, all matching resource types will be extracted. When passed as a ``function``, all resource
 *         types for which the function returned ``true`` will be extracted.
 *
 * @return a coherent sub-set of the AWS CloudFormation Resource specification, including all property types related
 *     to the selected resource types.
 */
export function filteredSpecification(filter: string | RegExp | Filter): schema.Specification {
  const spec = specification();

  const result: schema.Specification = { ResourceTypes: {}, PropertyTypes: {}, Fingerprint: spec.Fingerprint };
  const predicate: Filter = makePredicate(filter);
  for (const type of resourceTypes()) {
    if (!predicate(type)) { continue; }
    result.ResourceTypes[type] = spec.ResourceTypes[type];
    const prefix = `${type}.`;
    for (const propType of Object.keys(spec.PropertyTypes!).filter(n => n.startsWith(prefix))) {
      result.PropertyTypes[propType] = spec.PropertyTypes![propType];
    }
  }
  result.Fingerprint = crypto.createHash('sha256').update(JSON.stringify(result)).digest('base64');
  return result;
}

export type Filter = (name: string) => boolean;

/**
 * Creates a predicate function from a given filter.
 *
 * @param filter when provided as a ``string``, performs an exact match comparison.
 *         when provided as a ``RegExp``, performs uses ``str.match(RegExp)``.
 *         when provided as a ``function``, use the function as-is.
 *
 * @returns a predicate function.
 */
function makePredicate(filter: string | RegExp | Filter): Filter {
  if (typeof filter === 'string') {
    return s => s === filter;
  } else if (typeof filter === 'function') {
    return filter as Filter;
  } else {
    return s => s.match(filter) != null;
  }
}

/**
 * Return the properties of the given type that require the given scrutiny type
 */
export function scrutinizablePropertyNames(resourceType: string, scrutinyTypes: schema.PropertyScrutinyType[]): string[] {
  const impl = specification().ResourceTypes[resourceType];
  if (!impl) { return []; }

  const ret = new Array<string>();

  for (const [propertyName, propertySpec] of Object.entries(impl.Properties || {})) {
    if (scrutinyTypes.includes(propertySpec.ScrutinyType || schema.PropertyScrutinyType.None)) {
      ret.push(propertyName);
    }
  }

  return ret;
}

/**
 * Return the names of the resource types that need to be subjected to additional scrutiny
 */
export function scrutinizableResourceTypes(scrutinyTypes: schema.ResourceScrutinyType[]): string[] {
  const ret = new Array<string>();
  for (const [resourceType, resourceSpec] of Object.entries(specification().ResourceTypes)) {
    if (scrutinyTypes.includes(resourceSpec.ScrutinyType || schema.ResourceScrutinyType.None)) {
      ret.push(resourceType);
    }
  }

  return ret;
}

/**
 * Augmentations for a CloudFormation resource type
 */
export interface ResourceAugmentation {
  /**
   * Metric augmentations for this resource type
   */
  metrics?: ResourceMetricAugmentations;

  /**
   * Options for this resource augmentation
   *
   * @default no options
   */
  options?: AugmentationOptions;
}

export interface AugmentationOptions {
  /**
   * The name of the file containing the class to be "augmented".
   *
   * @default kebab cased CloudFormation resource name + '-base'
   */
  classFile?: string;

  /**
   * The name of the class to be "augmented".
   *
   * @default CloudFormation resource name + 'Base'
   */
  class?: string;

  /**
   * The name of the file containing the interface to be "augmented".
   *
   * @default - same as ``classFile``.
   */
  interfaceFile?: string;

  /**
   * The name of the interface to be "augmented".
   *
   * @default 'I' + CloudFormation resource name
   */
  interface?: string;
}

export interface ResourceMetricAugmentations {
  namespace: string;
  dimensions: {[key: string]: string};
  metrics: ResourceMetric[];
}

export interface ResourceMetric {
  /**
   * Uppercase-first metric name
   */
  name: string;

  /**
   * Documentation line
   */
  documentation: string;

  /**
   * Whether this is an even count (1 gets emitted every time something occurs)
   *
   * @default MetricType.Attrib
   */
  type?: MetricType;
}

export enum MetricType {
  /**
   * This metric measures an attribute of events
   *
   * It could be time, or request size, or similar. The default
   * aggregate for this type of event is "Avg".
   */
  Attrib = 'attrib',

  /**
   * This metric is counting events.
   *
   * This means the metric "1" is emitted every time an event occurs.
   * Only "Sum" is a meaningful aggregate of this type of metric.
   */
  Count = 'count',

  /**
   * This metric is emitting a size.
   *
   * The metric is not event-based, but measures some global ever-changing
   * property. The most useful aggregate of this type of metric is "Max".
   */
  Gauge = 'gauge'
}

export interface Documented {
  /** A link to the AWS CloudFormation User Guide that provides informations about the entity. */
  Documentation?: string;
}

export enum PrimitiveType {
  String = 'String',
  Long = 'Long',
  Integer = 'Integer',
  Double = 'Double',
  Boolean = 'Boolean',
  Timestamp = 'Timestamp',
  Json = 'Json'
}

export function isPrimitiveType(str: string): str is PrimitiveType {
  switch (str) {
    case PrimitiveType.String:
    case PrimitiveType.Long:
    case PrimitiveType.Integer:
    case PrimitiveType.Double:
    case PrimitiveType.Boolean:
    case PrimitiveType.Timestamp:
    case PrimitiveType.Json:
      return true;
    default:
      return false;
  }
}

/**
 * Additional resource information obtained from cfn-lint
 */
export interface CfnLintResourceAnnotations {
  /**
   * Whether or not the given resource is stateful
   */
  readonly stateful: boolean;

  /**
   * Whether or not a Delete operation requires the resource to be empty
   */
  readonly mustBeEmptyToDelete: boolean;
}

export * from './base-types';
export * from './property';
export * from './resource-type';
export * from './specification';
export * from './augmentation';
export * from './cfn-lint';

import { Documented, PrimitiveType } from './base-types';

export type Property = ScalarProperty | CollectionProperty;
export type ScalarProperty = PrimitiveProperty | ComplexProperty | UnionProperty;
export type CollectionProperty = ListProperty | MapProperty | UnionProperty;
export type ListProperty = PrimitiveListProperty | ComplexListProperty;
export type MapProperty = PrimitiveMapProperty | ComplexMapProperty;
export type ComplexMapProperty = MapOfStructs | MapOfListsOfPrimitives;
export type TagProperty = TagPropertyStandard | TagPropertyAutoScalingGroup | TagPropertyJson | TagPropertyStringMap;

export interface PropertyBase extends Documented {
  /**
   * Indicates whether the property is required.
   *
   * @default false
   */
  Required?: boolean;
  /**
   * During a stack update, the update behavior when you add, remove, or modify the property. AWS CloudFormation
   * replaces the resource when you change `mmutable``properties. AWS CloudFormation doesn't replace the resource
   * when you change ``Mutable`` properties. ``Conditional`` updates can be mutable or immutable, depending on, for
   * example, which other properties you updated.
   *
   * @default UpdateType.Mutable
   */
  UpdateType?: UpdateType;

  /**
   * During a stack update, what kind of additional scrutiny changes to this property should be subjected to
   *
   * @default None
   */
  ScrutinyType?: PropertyScrutinyType;
}

export interface PrimitiveProperty extends PropertyBase {
  /** The valid primitive type for the property. */
  PrimitiveType: PrimitiveType;
}

export interface ComplexProperty extends PropertyBase {
  /** The type of valid values for this property */
  Type: string;
}

export interface ListPropertyBase extends PropertyBase {
  /**
   * A list is a comma-separated list of values.
   */
  Type: 'List';
  /**
   * Indicates whether AWS CloudFormation allows duplicate values. If the value is ``true``, AWS CloudFormation
   * ignores duplicate values. if the value is  ``false``, AWS CloudFormation returns an arror if you submit duplicate
   * values.
   */
  DuplicatesAllowed?: boolean;
}

export interface PrimitiveListProperty extends ListPropertyBase {
  /** The valid primitive type for the property. */
  PrimitiveItemType: PrimitiveType;
}

export interface ComplexListProperty extends ListPropertyBase {
  /** Valid values for the property */
  ItemType: string;
}

export interface MapPropertyBase extends PropertyBase {
  /** A map is a set of key-value pairs, where the keys are always strings. */
  Type: 'Map';
  /**
   * Indicates whether AWS CloudFormation allows duplicate values. If the value is ``true``, AWS CloudFormation
   * ignores duplicate values. if the value is  ``false``, AWS CloudFormation returns an arror if you submit duplicate
   * values.
   */
  DuplicatesAllowed?: false;
}

export interface PrimitiveMapProperty extends MapPropertyBase {
  /** The valid primitive type for the property. */
  PrimitiveItemType: PrimitiveType;
}

export interface MapOfStructs extends MapPropertyBase {
  /** Valid values for the property */
  ItemType: string;
}

export interface MapOfListsOfPrimitives extends MapPropertyBase {
  /** The type of the map values, which in this case is always 'List'. */
  ItemType: string;

  /** The valid primitive type for the lists that are the values of this map. */
  PrimitiveItemItemType: PrimitiveType;
}

export interface TagPropertyStandard extends PropertyBase {
  ItemType: 'Tag' | 'TagsEntry' | 'TagRef' | 'ElasticFileSystemTag' | 'HostedZoneTag';
  Type: 'Tags';
}

export interface TagPropertyAutoScalingGroup extends PropertyBase {
  ItemType: 'TagProperty';
}

export interface TagPropertyJson extends PropertyBase {
  PrimitiveType: PrimitiveType.Json;
}

export interface TagPropertyStringMap extends PropertyBase {
  PrimitiveItemType: 'String';
}

/**
 * A property type that can be one of several types. Currently used only in SAM.
 */
export interface UnionProperty extends PropertyBase {
  /** Valid primitive types for the property */
  PrimitiveTypes?: PrimitiveType[];
  /** Valid complex types for the property */
  Types?: string[]
  /** Valid primitive item types for this property */
  PrimitiveItemTypes?: PrimitiveType[];
  /** Valid list item types for the property */
  ItemTypes?: string[];
}

export enum UpdateType {
  Conditional = 'Conditional',
  Immutable = 'Immutable',
  Mutable = 'Mutable'
}

export function isUpdateType(str: string): str is UpdateType {
  switch (str) {
    case UpdateType.Conditional:
    case UpdateType.Immutable:
    case UpdateType.Mutable:
      return true;
    default:
      return false;
  }
}

export function isScalarProperty(prop: Property): prop is ScalarProperty {
  return isPrimitiveProperty(prop)
    || isComplexProperty(prop)
    // A UnionProperty is only Scalar if it defines Types or PrimitiveTypes
    || (isUnionProperty(prop) && !!(prop.Types || prop.PrimitiveTypes));
}

export function isPrimitiveProperty(prop: Property): prop is PrimitiveProperty {
  return !!(prop as PrimitiveProperty).PrimitiveType;
}

export function isComplexProperty(prop: Property): prop is ComplexProperty {
  const propType = (prop as ComplexProperty).Type;
  return propType != null && propType !== 'Map' && propType !== 'List';
}

export function isCollectionProperty(prop: Property): prop is CollectionProperty {
  return isListProperty(prop)
    || isMapProperty(prop)
    // A UnionProperty is only Collection if it defines ItemTypes or PrimitiveItemTypes
    || (isUnionProperty(prop) && !!(prop.ItemTypes || prop.PrimitiveItemTypes));
}

export function isListProperty(prop: Property): prop is ListProperty {
  return (prop as ListProperty).Type === 'List';
}

export function isPrimitiveListProperty(prop: Property): prop is PrimitiveListProperty {
  return isListProperty(prop) && !!(prop as PrimitiveListProperty).PrimitiveItemType;
}

export function isComplexListProperty(prop: Property): prop is ComplexListProperty {
  return isListProperty(prop) && !!(prop as ComplexListProperty).ItemType;
}

export function isMapProperty(prop: Property): prop is MapProperty {
  return (prop as MapProperty).Type === 'Map';
}

export function isPrimitiveMapProperty(prop: Property): prop is PrimitiveMapProperty {
  return isMapProperty(prop) && !!(prop as PrimitiveMapProperty).PrimitiveItemType;
}

export function isMapOfStructsProperty(prop: Property): prop is MapOfStructs {
  return isMapProperty(prop) &&
    !isPrimitiveMapProperty(prop) &&
    !isMapOfListsOfPrimitivesProperty(prop);
}

// note: this (and the MapOfListsOfPrimitives type) are not actually valid in the CFN spec!
// they are only here to support our patch of the CFN spec
// to alleviate https://github.com/aws/aws-cdk/issues/3092
export function isMapOfListsOfPrimitivesProperty(prop: Property): prop is MapOfListsOfPrimitives {
  return isMapProperty(prop) && (prop as ComplexMapProperty).ItemType === 'List';
}

export function isUnionProperty(prop: Property): prop is UnionProperty {
  const castProp = prop as UnionProperty;
  return !!(castProp.ItemTypes || castProp.PrimitiveTypes || castProp.Types);
}

export enum PropertyScrutinyType {
  /**
   * No additional scrutiny
   */
  None = 'None',

  /**
   * This is an IAM policy directly on a resource
   */
  InlineResourcePolicy = 'InlineResourcePolicy',

  /**
   * Either an AssumeRolePolicyDocument or a dictionary of policy documents
   */
  InlineIdentityPolicies = 'InlineIdentityPolicies',

  /**
   * A list of managed policies (on an identity resource)
   */
  ManagedPolicies = 'ManagedPolicies',

  /**
   * A set of ingress rules (on a security group)
   */
  IngressRules = 'IngressRules',

  /**
   * A set of egress rules (on a security group)
   */
  EgressRules = 'EgressRules',
}

export function isPropertyScrutinyType(str: string): str is PropertyScrutinyType {
  return (PropertyScrutinyType as any)[str] !== undefined;
}

const tagPropertyNames = {
  FileSystemTags: '',
  HostedZoneTags: '',
  Tags: '',
  UserPoolTags: '',
};

export type TagPropertyName = keyof typeof tagPropertyNames;

export function isTagPropertyName(name?: string): name is TagPropertyName {
  if (undefined === name) {
    return false;
  }
  return tagPropertyNames.hasOwnProperty(name);
}
/**
 * This function validates that the property **can** be a Tag Property
 *
 * The property is only a Tag if the name of this property is Tags, which is
 * validated using `ResourceType.isTaggable(resource)`.
 */
export function isTagProperty(prop: Property): prop is TagProperty {
  return (
    isTagPropertyStandard(prop) ||
    isTagPropertyAutoScalingGroup(prop) ||
    isTagPropertyJson(prop) ||
    isTagPropertyStringMap(prop)
  );
}

export function isTagPropertyStandard(prop: Property): prop is TagPropertyStandard {
  return (
    (prop as TagPropertyStandard).ItemType === 'Tag' ||
    (prop as TagPropertyStandard).ItemType === 'TagsEntry' ||
    (prop as TagPropertyStandard).Type === 'Tags' ||
    (prop as TagPropertyStandard).ItemType === 'TagRef' ||
    (prop as TagPropertyStandard).ItemType === 'ElasticFileSystemTag' ||
    (prop as TagPropertyStandard).ItemType === 'HostedZoneTag'
  );

}

export function isTagPropertyAutoScalingGroup(prop: Property): prop is TagPropertyAutoScalingGroup {
  return (prop as TagPropertyAutoScalingGroup).ItemType === 'TagProperty';
}

export function isTagPropertyJson(prop: Property): prop is TagPropertyJson {
  return (prop as TagPropertyJson).PrimitiveType === PrimitiveType.Json;
}

export function isTagPropertyStringMap(prop: Property): prop is TagPropertyStringMap {
  return (prop as TagPropertyStringMap).PrimitiveItemType === 'String';
}

import { Documented, PrimitiveType } from './base-types';
import { isTagProperty, isTagPropertyName, Property, TagProperty } from './property';

export interface ResourceType extends Documented {
  /**
   * The attributes exposed by the resource type, if any.
   */
  Attributes?: { [name: string]: Attribute };
  /**
   * The properties accepted by the resource type, if any.
   */
  Properties?: { [name: string]: Property };
  /**
   * The ``Transform`` required by the resource type, if any.
   */
  RequiredTransform?: string;

  /**
   * What kind of value the 'Ref' operator refers to, if any.
   */
  RefKind?: string;

  /**
   * During a stack update, what kind of additional scrutiny changes to this resource should be subjected to
   *
   * @default None
   */
  ScrutinyType?: ResourceScrutinyType;
}

export interface TaggableResource extends ResourceType {
  Properties: {
    FileSystemTags: TagProperty;
    HostedZoneTags: TagProperty;
    Tags: TagProperty;
    UserPoolTags: TagProperty;
    [name: string]: Property;
  }
}

export type Attribute = PrimitiveAttribute | ListAttribute | MapAttribute;

export interface PrimitiveAttribute {
  PrimitiveType: PrimitiveType;
}

export type ListAttribute = PrimitiveListAttribute | ComplexListAttribute;

export interface PrimitiveListAttribute {
  Type: 'List';
  PrimitiveItemType: PrimitiveType;
}

export interface ComplexListAttribute {
  Type: 'List';
  ItemType: string;
}

export type MapAttribute = PrimitiveMapAttribute;

export interface PrimitiveMapAttribute {
  Type: 'Map';
  PrimitiveItemType: PrimitiveType;
}

/**
 * Determine if the resource supports tags
 *
 * This function combined with isTagProperty determines if the `cdk.TagManager`
 * and `cdk.TaggableResource` can process these tags. If not, standard code
 * generation of properties will be used.
 */
export function isTaggableResource(spec: ResourceType): spec is TaggableResource {
  if (spec.Properties === undefined) {
    return false;
  }
  for (const key of Object.keys(spec.Properties)) {
    if (isTagPropertyName(key) && isTagProperty(spec.Properties[key])) {
      return true;
    }
  }
  return false;
}

export function isPrimitiveAttribute(spec: Attribute): spec is PrimitiveAttribute {
  return !!(spec as PrimitiveAttribute).PrimitiveType;
}

export function isListAttribute(spec: Attribute): spec is ListAttribute {
  return (spec as ListAttribute).Type === 'List';
}

export function isMapAttribute(spec: Attribute): spec is MapAttribute {
  return (spec as MapAttribute).Type === 'Map';
}

export function isPrimitiveListAttribute(spec: Attribute): spec is PrimitiveListAttribute {
  return isListAttribute(spec) && !!(spec as PrimitiveListAttribute).PrimitiveItemType;
}

export function isComplexListAttribute(spec: Attribute): spec is ComplexListAttribute {
  return isListAttribute(spec) && !!(spec as ComplexListAttribute).ItemType;
}

export function isPrimitiveMapAttribute(spec: Attribute): spec is PrimitiveMapAttribute {
  return isMapAttribute(spec) && !!(spec as PrimitiveMapAttribute).PrimitiveItemType;
}

/**
 * Type declaration for special values of the "Ref" attribute represents.
 *
 * The attribute can take on more values than these, but these are treated specially.
 */
export enum SpecialRefKind {
  /**
   * No '.ref' member is generated for this type, because it doesn't have a meaningful value.
   */
  None = 'None',

  /**
   * The generated class will inherit from the built-in 'Arn' type.
   */
  Arn = 'Arn'
}

export enum ResourceScrutinyType {
  /**
   * No additional scrutiny
   */
  None = 'None',

  /**
   * An externally attached policy document to a resource
   *
   * (Common for SQS, SNS, S3, ...)
   */
  ResourcePolicyResource = 'ResourcePolicyResource',

  /**
   * This is an IAM policy on an identity resource
   *
   * (Basically saying: this is AWS::IAM::Policy)
   */
  IdentityPolicyResource = 'IdentityPolicyResource',

  /**
   * This is a Lambda Permission policy
   */
  LambdaPermission = 'LambdaPermission',

  /**
   * An ingress rule object
   */
  IngressRuleResource = 'IngressRuleResource',

  /**
   * A set of egress rules
   */
  EgressRuleResource = 'EgressRuleResource',
}

export function isResourceScrutinyType(str: string): str is ResourceScrutinyType {
  return (ResourceScrutinyType as any)[str] !== undefined;
}

import { Documented } from './base-types';
import { Property } from './property';
import { ResourceType } from './resource-type';

/**
 * @see https://docs.aws.amazon.com/AWSCloudFormation/latest/UserGuide/cfn-resource-specification-format.html
 */
export interface Specification {
  /**
   * A fingerprint of the template, that can be used to determine whether the template has changed.
   */
  Fingerprint: string;
  /**
   * For resources that have properties within a property (also known as subproperties), a list of subproperty
   * specifications, such as which properties are required, the type of allowed value for each property, and their
   * update behavior.
   */
  PropertyTypes: { [name: string]: PropertyType };
  /**
   * The list of resources and information about each resource's properties, such as it's property names, which
   * properties are required, and their update behavior.
   */
  ResourceTypes: { [name: string]: ResourceType };
}

/**
 * Describing a user-defined property type
 *
 * Even though looks weird, the CloudFormation spec does not make a distinction between properties and
 * property TYPES: https://docs.aws.amazon.com/AWSCloudFormation/latest/UserGuide/cfn-resource-specification-format.html
 *
 * That means that a "type" comes with fields such as "Required", "UpdateType Mutable", etc
 * (even though those only make sense for a particular PROPERTY of that type). They only seem to occur
 * on non-Record properties though.
 *
 * In practice, even though aliases for Primitive properties are allowed, only RecordProperties
 * and CollectionProperties seem to actually occur in the spec in the "types" section.
 */
export type PropertyType = RecordProperty | Property;

/**
 * The specifications of a property object type.
 */
export interface RecordProperty extends Documented {
  /**
   * The properties of the Property type.
   */
  Properties: { [name: string]: Property };
}

/**
 * Whether the given type definition is a Record property
 */
export function isRecordType(propertyType: PropertyType): propertyType is RecordProperty {
  return 'Properties' in propertyType;
}
